Fields.formula:
var factor=1
var base=41234
var additional=0

if ( "${gender}" == "female") {
factor-=0.1
}

switch("${experience}") {
case "SKILLED": 
factor+=0.5
break;
case "SENIOR": 
factor+=1
break;
default:
factor+=0.1
}

if ( "${specialty}" == "Kids") {
additional+=1000
}

if ( "${specialty}" == "Women's dress" && "${gender}" == "FEMALE") {
additional+=2000
}

if ( "${specialty}" == "Women's top" && "${gender}" == "MALE") {
additional+=800
}

if ( "${specialty}" == "Men's shirts" && "${gender}" == "MALE") {
additional-=500
}

if ( "${specialty}" == "Men's pants" && "${gender}" == "FEMALE") {
additional+=1500
}

if ( "${specialty}" == "Jackets & Coats") {
factor+=0.3
}


Math.round((base + additional)*factor)
